import React, { useState,useEffect } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';
const Read = () => {
    const[id,setId]=useState("");
    const[name,setName]=useState("");
    const[email,setEmail]=useState("");
    const[data,setData]=useState([]);
    useEffect(()=>{
        setId(localStorage.getItem("id"));
        setName(localStorage.getItem("name"));
        setEmail(localStorage.getItem("email"));
    })


    function getData(){
        axios.get("https://6449e5fe79279846dcda7b28.mockapi.io/crud-youtuber")
        .then((res)=>{
            setData(res.data);
        })
    }
    function handleDelete(id){
        axios.delete(`https://6449e5fe79279846dcda7b28.mockapi.io/crud-youtuber/${id}`)
        .then(()=>{
            getData();
        })
    }
     const localStorage=(id,name,email)=>{
        localStorage.setItem("id",id);
        localStorage.setItem("name",name);
        localStorage.setItem("email",email);

     }
    useEffect(()=>{

        getData();
    },[data]);

  return (
    <>
         <h2>Read Operation</h2>
        <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Name</th>
      <th scope="col">Email</th>
      <th scope="col"></th>
      <th scope="col"></th>
    </tr>
  </thead>
  {
    data.map((eachdata)=>{
        return(
            <>
             <tbody>
           <tr>
              <th scope="row">{eachdata.id}</th>
                <td>{eachdata.name}</td>
                  <td>{eachdata.email}</td>
                  <Link to="/update">
                  <td><button className="btn-success" onClick={()=>localStorage(eachdata.id,eachdata.name,eachdata.email)}>Edit</button></td>
                  </Link>

                   <td><button className="btn-danger" onClick={()=>handleDelete(eachdata.id)}>Delete</button></td>
            </tr>


  </tbody>
            </>
        )
    })
  }
</table>

    </>
  )
}

export default Read
