import React, { useState,useEffect } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';
const Read = () => {

    const[data,setData]=useState([]);
    const[tabledark,setTabledark]=useState('');


    function getData(){
        axios.get("https://6449e5fe79279846dcda7b28.mockapi.io/crud-youtuber")
        .then((res)=>{
            setData(res.data);
        })
    }

    function handleDelete(id){
        axios.delete(`https://6449e5fe79279846dcda7b28.mockapi.io/crud-youtuber/${id}`)
        .then(()=>{
            getData();
        })
    }
    const setToLocalStorage=(id,name,email)=>{
        localStorage.setItem("id",id);
        localStorage.setItem("name",name);
        localStorage.setItem("email",email);

    }
    useEffect(()=>{

        getData();
    },[data]);

  return (
    <>
       <div class="form-check form-switch">
  <input className="form-check-input" type="checkbox" onClick={()=>{
      if(tabledark==="table-dark")
      setTabledark(" ");
      else
      setTabledark("table-dark");
  }}/>

</div>

         <div className="d-flex justify-content-between m-2">
         <h2>Read Operation</h2>
        <Link to="/create">
        <button className="btn btn-primary">Create</button>
        </Link>
        </div>
        <table class={`table ${tabledark}`}>
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Name</th>
      <th scope="col">Email</th>
      <th scope="col"></th>
      <th scope="col"></th>
    </tr>
  </thead>
  {
    data.map((eachdata)=>{
        return(
            <>
             <tbody>
              <tr>
              <th scope="row">{eachdata.id}</th>
                <td>{eachdata.name}</td>
                  <td>{eachdata.email}</td>
                  <Link to="/update">
                  <td><button className="btn-success" onClick={()=>setToLocalStorage(eachdata.id,eachdata.name,eachdata.email)
    }            >Edit</button></td>
                  </Link>

                   <td><button className="btn-danger" onClick={()=>handleDelete(eachdata.id)}>Delete</button></td>
                  </tr>


  </tbody>
            </>
        )
    })
  }
</table>

    </>
  )
}

export default Read
